class Solution:
    def countServers(self, n: int, logs: List[List[int]], x: int, queries: List[int]) -> List[int]:
        events = [(t + x, 1, sid) for sid, t in logs] + list(zip(queries, range(len(queries)), range(n, 2 * n)))
