class Solution:
    def maxNonDecreasingLength(self, nums1: List[int], nums2: List[int]) -> int:
        n = len(nums1)
        dp1 = [1] * n  # Maximum length with nums1 at the end
        dp2 = [1] * n  # Maximum length with nums2 at the end

        for i in range(1, n):
            dp1[i], dp2[i] = 1, 1  # Default values, might get updated
            # Check if nums1[i] extends a non-decreasing subarray ending in nums1[i-1] or nums2[i-1]
            if nums1[i] >= nums1[i-1]:
                dp1[i] = max(dp1[i], dp1[i-1] + 1)
            # Check if nums1[i] extends a non-decreasing subarray ending in nums2[i-1]
            if nums1[i] >= nums2[i-1]:
                dp1[i] = max(dp1[i], dp2[i-1] + 1)

            # Check if nums2[i] extends a non-decreasing subarray ending in nums1[i-1] or nums2[i-1]
            if nums2[i] >= nums1[i-1]:
                dp2[i] = max(dp2[i], dp1[i-1] + 1)
            # Check if nums2[i] extends a non-decreasing subarray ending in nums2[i-1]
            if nums2[i] >= nums2[i-1]:
                dp2[i] = max(dp2[i], dp2[i-1] + 1)

        # The maximum length is the max value in dp1 and dp2 arrays
        return max(max(dp1), max(dp2))
