                graph[orig][chng] = c
                graph[chng][orig] = c

        def dijkstra(s, d):
            if s not in graph and d not in graph:
                return -inf

            dist = defaultdict(lambda: inf)
            dist[s] = 0
            heap = [(0, s)]

