            high = int(str(min(limit, int(s[0]))) + ('9' * (len(s) - 1)))
            return total_count + (max(0, min(finish + 1, high) - max(low, start)) // factor)

        return countPowerfulInt(finish, limit, s) - countPowerfulInt(start - 1, limit, s)
