class Solution:
    def maximumLength(self, s: str) -> int:
        def get_valid_substrings(sub):
            if len(set(sub)) > 1:
                return set()
            return {sub[i:j] for i in range(len(sub)) for j in range(i+1, len(sub)+1)}

