        col_cost = float('inf')
        if m > 1:
            col_cost = sum(min(row[i] ^ row[~i] for row in grid[1:])
                           for i in range(n // 2))
            if n % 2 == 1:
                col_cost += min(grid[0][n // 2], sum(row[n // 2] for row in grid[1:]))

        return min(row_cost, col_cost)

