class Solution:
    def stringHash(self, s: str, k: int) -> str:
        result = ""
        for i in range(0, len(s), k):
            sub = s[i:i+k]
            sum_hash = 0
            for c in sub:
                sum_hash += ord(c) - ord('a')
            hashed_char = sum_hash % 26
            result += chr(hashed_char + ord('a'))
        return result[len(result) % k:]
