from collections import Counter

class Solution:
    def countGoodIntegers(self, n: int, k: int) -> int:
        dp = [0] * 2001  # Maximum possible n is 10, so 2001 is enough for dp array
        dp[0] = 1
        mod = 10 ** 9 + 7
        ans = 0

        for i in range(n//2):
            # We skip the calculation for the middle digit in case of odd n
            if n % 2 == 1 and i == n//2 - 1:
                continue

            for d1 in range(1, 10):
                for d2 in range(10):
                    if d1 % k == 0 or (k * d1 % 10) == d2 or ((k * d2) % 10) == d1:
                        pickBoth = dp[i*2] if d1 == d2 else 2 * dp[i*2]
                        dp[i*2+2] = (dp[i*2+2] + pickBoth) % mod

            for d in range(1 if i == 0 else 0, 10):
                if (k * d) % 10 == d or (d * k) % 10 == d:
                    dp[i*2 + 1] = (dp[i*2 + 1] + dp[i*2]) % mod

        if n % 2 == 1:
            for d in range(1, 10):
                ans = (ans + dp[n-1]* (d * k) % 10 == d) % mod
        else:
            ans = dp[n]

        return ans
