class Solution:
    def maxGoodNumber(self, nums: List[int]) -> int:
        def zl(s1, s2):
            sign1 = 1 if s1[0] != "-" else -1
            sign2 = 1 if s2[0] != "-" else -1
            n1 = len(s1)
            n2 = len(s2)
            for i in range(abs(n1 - n2)):
                if n1 > n2:
                    s2 = "0" + s2
                else:
                    s1 = "0" + s1
            for i in range(len(s1)):
                if s1[i] != s2[i]:
                    return sign1 * (ord(s1[i]) > ord(s2[i]))
            return 0
        m = "0"
        for n in nums:
            s = format(n, 'b')
            if zl(m+s, m) > 0:
                m = m+s
            elif zl(s+m, m) > 0:
                m = s+m
            else:
                m = max(m, s)
        return int(m, 2)
