            val = indices[i-1] + 1 if i > 0 else -1
            pos = lower_bound(d[c], val)
            if pos == len(d[c]):
                if i == len(word2) - 1 and c in d:
                    indices[i] = d[c][-1]
                else:
                    return []
            else:
                indices[i] = d[c][pos]

        if len(set(indices)) == 1 and indices[0] == -1:
            return []

        return indices

