    def maxDistinctElements(self, nums: List[int], k: int) -> int:
        nums.sort()
        cnt = [0]
        ans = 0
        invalid = 0
        def check(x):
            nonlocal invalid
            if x == 0:
                invalid = 0
                return k * (cnt[x] - 1) + nums[cnt[x] - 1] - nums[0] <= 2 * k
            invalid = cnt[x - 1]
            if k * (cnt[x] - invalid - 1) + nums[cnt[x] - 1] - nums[invalid] > 2 * k:
