from typing import List

class Solution:
    def minArraySum(self, nums: List[int], k: int, op1: int, op2: int) -> int:
        nums.sort()
        n = len(nums)

        # Count how many times operation 2 can be used on each number
        operation_2_counts = [0] * n
        for i in range(n):
            if nums[i] >= k:
                operation_2_counts[i] = nums[i] // k
                nums[i] %= k

        total_sum = sum(nums)  # Initial total sum

        # Use operation 1 as many times as possible, prioritizing higher numbers
        for i in range(n - 1, -1, -1):
            if nums[i] > 0 and op1 > 0:
                nums[i] = (nums[i] + 1) // 2
                total_sum -= nums[i]
                op1 -= 1

        # Use operation 2 on the largest numbers possible
        indices_sorted_by_op2 = sorted(range(n), key=lambda i: -operation_2_counts[i])
        for i in indices_sorted_by_op2:
            if op2 > 0 and operation_2_counts[i] > 0:
                nums[i] -= min(k, nums[i])
                total_sum -= min(k, nums[i])
                op2 -= 1

        return total_sum + sum(nums)
