            return x
        else:
            self.par[x] = self.find(self.par[x])
            return self.par[x]

    def union(self, x, y):
        x = self.find(x)
        y = self.find(y)
        if x == y:
            return

        if self.rank[x] < self.rank[y]:
            self.par[x] = y
            self.group_size[y] += self.group_size[x]
        else:
            self.par[y] = x
            self.group_size[x] += self.group_size[y]
            if self.rank[x] == self.rank[y]:
                self.rank[x] += 1
        self.groups -= 1

