        self.val = modmat.zeros(M, M)
        self.N, self.M = N, M
        self.M3 = M ** 3
        self.D = modmat.get_D(N)
        self.DE = modmat.get_DE(N)
        self.Dinv = modmat.get_Dinv(N)
