Q = collections.namedtuple('Q', ['x', 'y', 'z', 'id'])
def gen_cuboids(input_iter):
    N = next(input_iter)
    for i, line in enumerate(input_iter):
        *coords, = map(int, line.split())
        yield i, Q(_min=Q(_min=coords[0], _max=coords[3], y=Q(_min=coords[1], _max=coords[4]), z=Q(_min=coords[2], _max=coords[5])), _max=Q(_min=coords[3], _max=coords[0], y=Q(_min=coords[4], _max=coords[1]), z=Q(_min=coords[5], _max=coords[2])), id=i)

def partition_cuboids(cuboids, attr):
    p0 = {}
    p1 = {}
    [p0.update({_c[attr]._min._min:None}) for _id, _c in cuboids]
    [p0.update({_c[attr]._max._max:None}) for _id, _c in cuboids]
    for _id, _c in cuboids:
        p1[_c[attr]._min._min] = _c
        p1[_c[attr]._max._max] = _c
    p2 = {}
    init_counter = 0
    [p2.update({_p:init_counter}) for _p in sorted(p0)]
    [_c[id].update({_attr:p2[_k]}) for _k, _c[id] in p1.items()]
    return p2, sorted(p0)

def map_cuboids(cuboids, p2):
