def main():
    N, M = map(int, input().split())
    graph = defaultdict(set)
    indegrees = [0] * N

    for _ in range(M):
        A, B = map(int, input().split())
        A -= 1
        B -= 1
        graph[B].add(A)
        indegrees[A] += 1

    potential_strongest = [i for i in range(N) if indegrees[i] == 0]

    if len(potential_strongest) > 1:
        winner = -1
    else:
        winner = potential_strongest[0]

    for vertex in graph:
        if indegrees[vertex] == 0:
            if len(graph[vertex]) == N - 1:
                winner = vertex
                break
            else:
                winner = -1
                break

    print(winner + 1)

