def polyomino_search(board, polyominoes):
    def rotate_table(poly_table, d):
        if d == 0:
            return poly_table
        return rotate_table(np.rot90(poly_table, k=1), d-1)

    def update_board(poly_id, x, y, d):
        for i, row in enumerate(poly_table):
            for j, value in enumerate(row):
                if value and board[(x+i) % 4][(y+j) % 4] and (x+i) % 4 and (y+j) % 4:
                    return
                elif value and not board[(x+i) % 4][(y+j) % 4]:
                    board[(x+i) % 4][(y+j) % 4] = True
        output.append(tuple((poly_id, x, y, d)))

    output = []
    for poly_id, poly_table in enumerate(polyominoes):
        for d in range(4):
            for x, y in itertools.product(range(4),range(4)):
