def azerogpt():
    import sys
    input = sys.stdin.readline
    N = int(input())
    *events, = map(int, sys.stdin.read().split())
    events = list(zip(events[::2], events[1::2]))

    result = solve(N, events)
    print(*result, sep='\n')

def solve(N, events):
    stack = []
    counts = defaultdict(int)
    potions = [0]*N
    for i, (t, x) in enumerate(events):
        if t == 1:
            if stack and stack[-1][0] == x:
                stack.pop()
            else:
                stack.append((x, i))
                counts[stack[-1][0]] += 1
        else:
            if not stack or stack[-1][0] != x:
                potion_found = False
                while stack and counts[stack[-1][0]] == 0:
                    _, j = stack.pop()
                    if j > i:
                        return [-1]
                    counts[stack[-1][0]] += 1

                if not stack:
                    return [-1]

                _, j = stack.pop()
                potions[j] = 1
            else:
                _, j = stack.pop()
                potions[j] = 1
                counts[stack[-1][0]] += 1 if stack else 0

            if len(stack) > N:
                return [-1]

    max_potions = max([counts[key] for key in counts], default=0)
    if not counts:
        max_potions = 0

    return [max_potions, *potions]

