import sys
input = sys.stdin.readline

direction_dict = {"U": (-1, 0), "D": (1, 0), "L": (0, -1), "R": (0, 1)}

def main():
    H, W, N = map(int, input().split())
    T = input().strip()
    mat = [input().strip() for _ in range(H)]

    def is_inside(i, j):
        return 0 < i < H and 0 < j < W and mat[i][j] != "#"

    uf = {}

    def root(x):
        if x not in uf:
            uf[x] = x
        while uf[x] != x:
            x, uf[x] = uf[x], uf[uf[x]]
        return x

    def unite(x, y):
        rx, ry = root(x), root(y)
        if rx == ry:
            return
        uf[rx] = ry

    # Setting UF
    for i in range(1, H - 1):
        for j in range(1, W - 1):
            if mat[i][j] == "#":
                continue
            for d, (di, dj) in direction_dict.items():
                if is_inside(i + di, j + dj):
                    unite((i, j), (i + di, j + dj))

    i, j = 0, 0
    pos_set = set()
    while T:
        t = T[0]
        if t == "U" and T.startswith("UD"):
            for k in range(1, min(N + 1, 500)):
                if not is_inside(i - k, j):
                    break
                if not (i, j) == root((i, j)) and root((i - k, j)) != root((i, j)):
                    break
                pos_set.add((i - k, j))
                if not T.startswith("UD" * k):
                    break
            i -= k
            T = T[2 * k: ]
        elif t == "D" and T.startswith("DU"):
            for k in range(1, min(N + 1, 500)):
                if not is_inside(i + k, j):
                    break
                if not (i, j) == root((i, j)) and root((i + k, j)) != root((i, j)):
                    break
                pos_set.add((i + k, j))
                if not T.startswith("DU" * k):
                    break
            i += k
            T = T[2 * k: ]
        elif t == "L" and T.startswith("LR"):
            for k in range(1, min(N + 1, 500)):
                if not is_inside(i, j - k):
                    break
                if not (i, j) == root((i, j)) and root((i, j - k)) != root((i, j)):
                    break
                pos_set.add((i, j - k))
                if not T.startswith("LR" * k):
                    break
            j -= k
            T = T[2 * k: ]
        elif t == "R" and T.startswith("RL"):
            for k in range(1, min(N + 1, 500)):
                if not is_inside(i, j + k):
                    break
                if not (i, j) == root((i, j)) and root((i, j + k)) != root((i, j)):
                    break
                pos_set.add((i, j + k))
                if not T.startswith("RL" * k):
                    break
            j += k
            T = T[2 * k: ]
        else:
            di, dj = direction_dict[t]
            i += di
            j += dj
            T = T[1:]
            if (i, j) not in pos_set:
                pos_set.add((i, j))

    print(len(pos_set))

