from itertools import accumulate

def flip_accumulated_bits(accumulated_bits, start, end):
    # Flip the bits in the range [start, end] of the accumulated bits list
    accumulated_bits[end+1] = 1 - accumulated_bits[end+1]
    if start > 0:
        accumulated_bits[start-1] = 1 - accumulated_bits[start-1]
    return [1 - x if start <= i <= end else x for i, x in enumerate(accumulated_bits)]

def is_good_string(accumulated_bits, start, end):
    # Check if the substring [start, end] forms a good string.
    for i in range(start, end):
        if accumulated_bits[i] == accumulated_bits[i+1]:
            return "No"
    return "Yes"

def solve(N, Q, S, queries):
    # Convert the binary string to a list of integers and calculate the exclusive ors.
    bits = [int(c) for c in S]
    accumulated_bits = list(accumulate(bits, lambda x, y: x ^ y, initial=0))

    for query in queries:
        type, start, end = query
        if type == 1:
            # Perform flip operation on the substring and update accumulated bits.
            accumulated_bits = flip_accumulated_bits(accumulated_bits, start-1, end-1)
        else:
            # Check if the substring forms a good string and print the result.
            print(is_good_string(accumulated_bits, start-1, end))

if __name__ == "__main__":
    N, Q = map(int, input().split())
    S = input()
    queries = [list(map(int, input().split())) for _ in range(Q)]
    solve(N, Q, S, queries)
