    H, W, M = map(int, input().split())
    row_color = [0] * H  # Tracks the current color of each row
    column_color = [0] * W  # Tracks the current color of each column
    color_count = defaultdict(int)  # Tracks the count of each color on the grid
    distinct_colors = set()  # Set to keep track of all distinct colors

    for _ in range(M):
        T, A, X = map(int, input().split())
        if T == 1:
            # Update the color of all cells in the A-th row
            old_color = row_color[A - 1]
            for j in range(W):
                if column_color[j] == 0:
                    if row_color[A - 1] != 0:
                        color_count[row_color[A - 1]] -= 1
                        if row_color[A - 1] != X:
                            distinct_colors.remove(row_color[A - 1])
                    row_color[A - 1] = X
                    color_count[X] += 1
                    distinct_colors.add(X)
                elif column_color[j] == old_color and old_color != 0:
                    color_count[old_color] -= 1
                    if old_color != X:
                        distinct_colors.remove(old_color)
                    column_color[j] = X
                    color_count[X] += 1
                    distinct_colors.add(X)
        elif T == 2:
            # Update the color of all cells in the A-th column
            for i in range(H):
                if row_color[i] == 0:
                    if column_color[A - 1] != 0:
                        color_count[column_color[A - 1]] -= 1
                        if column_color[A - 1] != X:
