from collections import deque

def bfs(grid, n, meds, h, w):
    dirs = [(0, 1), (1, 0), (0, -1), (-1, 0)]
    dist = [float("inf") for _ in range(n + 2)]
    dist[n] = 0

    q = deque([n])

    while q:
        from_node = q.popleft()

        r, c = meds[from_node] if from_node < n else (None, None)

        if from_node == n - 1:
            return dist[n - 1] != float('inf')

        if from_node < n:
            for dr, dc in dirs:
                nr, nc = r + dr, c + dc
                if 0 <= nr < h and 0 <= nc < w and grid[nr][nc] != "#":
                    to_node = meds.index((nr, nc)) if (nr, nc) in meds else -1

                    if to_node == -1 and dist[from_node] > 0:
                        if dist[n] > dist[from_node] - 1:
                            dist[n] = dist[from_node] - 1
                            q.appendleft(n)

                    elif dist[to_node] > dist[from_node] + meds[from_node][2]:
                        dist[to_node] = dist[from_node] + meds[from_node][2]
                        q.append(to_node)

    return False

def main():
    h, w = map(int, input().split())
    grid = [input() for _ in range(h)]

    start = None
    for i, row in enumerate(grid):
        for j, cell in enumerate(row):
            if cell == "S":
                start = (i, j)
                grid[i] = grid[i][:j] + "." + grid[i][j + 1:]
            elif cell == "T":
                end = (i, j)
                grid[i] = grid[i][:j] + "." + grid[i][j + 1:]

    n = int(input())
    meds = [tuple(map(int, input().split()))[::-1] for _ in range(n)]  # Store (energy, r, c) for easier access
    meds.append(start)
    meds.append(end)

    print("Yes" if bfs(grid, n, meds, h, w) else "No")

if __name__ == "__main__":
    main()
