import numpy as np

def score_board(arr_3x3):
    arr = np.array(arr_3x3)
    score = 0
    for i in range(3):
        for j in range(3):
            if arr[i, j] > 0:
                score += 1
            elif arr[i, j] < 0:
                score -= 1
    return score

def check_win(arr):
    for i in range(3):
        if sum(arr[i]) == 3:
            return 1
        if sum(arr[i]) == -3:
            return -1
        if sum([arr[j][i] for j in range(3)]) == 3:
            return 1
        if sum([arr[j][i] for j in range(3)]) == -3:
            return -1
    if arr[0, 0] + arr[1, 1] + arr[2, 2] == 3 or arr[0, 2] + arr[1, 1] + arr[2, 0] == 3:
        return 1
    if arr[0, 0] + arr[1, 1] + arr[2, 2] == -3 or arr[0, 2] + arr[1, 1] + arr[2, 0] == -3:
        return -1
    return 0

def play_game(arr_3x3, is_takahashi):
    if check_win(arr_3x3) != 0:
        return check_win(arr_3x3)
    if score_board(arr_3x3) == 0:
        return 0
    if is_takahashi:
        best_score = -1
        for i in range(3):
            for j in range(3):
                if arr_3x3[i][j] > 0:
                    arr_3x3_cpy = [[x for x in row] for row in arr_3x3]
                    arr_3x3_cpy[i][j] = 1
                    cur_score = play_game(arr_3x3_cpy, False)
                    if cur_score > best_score:
                        best_score = cur_score
        return best_score
    else:
        best_score = 1
        for i in range(3):
            for j in range(3):
                if arr_3x3[i][j] < 0:
                    arr_3x3_cpy = [[x for x in row] for row in arr_3x3]
                    arr_3x3_cpy[i][j] = -1
                    cur_score = play_game(arr_3x3_cpy, True)
                    if cur_score < best_score:
                        best_score = cur_score
        return best_score


def main():
    arr_3x3 = [list(map(int, input().split())) for _ in range(3)]
    verdict = play_game(arr_3x3, True)
    if verdict == 1:
        print('Takahashi')
    elif verdict == -1:
        print('Aoki')
    else:
        print('Draw')

if __name__ == "__main__":
    main()
