                heappush(q, G[i][j])

    anniversary = [[] for _ in range(Y + 1)]
    while q:
        x, i, j = heappop(q)
        if x > Y:
            break

        anniversary[x].append((i, j))
        for di, dj in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
            ni, nj = i + di, j + dj
            if ni < 0 or nj < 0 or ni >= H or nj >= W:
                continue
            if (ni, nj) not in visited:
                visited.add((ni, nj))
                _, ni, nj = G[ni][nj]
                heappush(q, (x, ni, nj))

    ans = [set() for _ in range(Y + 1)]
    for idx, level in enumerate(anniversary[1:]):
        for i, j in level:
            ans[idx + 1].add((i, j))

    area = [H * W]
    for i in range(1, len(anniversary) - 1):
        area.append(area[-1] - len(anniversary[i]))

    for answer in ans[1:]:
        next_area = 0
        while area[-1] != next_area:
            next_area = area.pop()
            for i, j in answer:
                for di, dj in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
                    ni, nj = i + di, j + dj
                    if ni < 0 or nj < 0 or ni >= H or nj >= W:
                        continue
                    if (ni, nj) not in visited:
                        visited.add((ni, nj))
                        area[-1] -= 1

        print(next_area)

