##直径は最も深い頂点から最遠点までの距離と定義
def calcmax(n, cnt):
    if not edges[n]:
        if n not in far:
            far[n] = (cnt, n)
        else:
            far[n] = max(far[n], (cnt, n))
        return (n, cnt)

    nextcandidates = [(n, cnt)]
    for n2, c in edges[n]:
        l = calcmax(n2, cnt+c)
        nextcandidates.append(l)

    m1 = max(nextcandidates, key=lambda x: x[1])
    m2 = sorted(nextcandidates, key=lambda x: x[1], reverse=True)[1]
    memo[(n1, n2)] = m1[1] + m2[1]
    memo[(n2, n1)] = m1[1] + m2[1]
    m1n, m1v = m1
    return (m1n, m1v)
