    global maxv, maxs, over
    candidates = []
    for n2, c in edges[n]:
        if n2 == avoid:
            continue
        nextcnt = cnt+c
        if not over and maxs[n2] < nextcnt:
            maxs[n2] = nextcnt
            maxv = max(maxv, nextcnt)
            if maxv >= MAX:
