        l = dfs(n2, nextcnt, n)
        candidates.append(l)
    if not candidates:
        return next((x for x in memo2 if n in x), None)
    m1 = max(candidates, key=lambda x: memo2[x])
    m2 = sorted(candidates, key=lambda x: memo2[(x)], reverse=True)[1]

    return (m1, m2)
