        inserted = False
        for j, (Ax, Bx, Cy, Dy) in enumerate(pairs):
            if check_intersect((A, B), (C, D), (Ax, Bx), (Cy, Dy)):
                p = pairs[:j] + [(A, B, C, D)] + pairs[j:]
                assert len(p) == N + 1
                p_j = min(p, key=lambda p: p[2])
                pairs = [p_j] + [p[i] for i in range(N + 1) if p[i] != p_j]
