class Combination(object):

    def __init__(self, N, mod=INT_MAX):
        self.mod = mod
        self.N = N
        self.sqrtN = int(N ** .5) + 1
        self.fact = [1]*(N+1)
        for i in range(1,len(self.fact)):
            self.fact[i] = self.fact[i-1]*i % self.mod
        self.factInv = [1]*(N+1)
        self.factInv[self.N] = pow(self.fact[N], self.mod - 2, self.mod)
        self.sqrtFactInv = [1]*(self.sqrtN)
        for i in range(self.N, self.sqrtN-1,-1):
            self.factInv[i-1] = self.factInv[i]*i % self.mod
        for i in range(self.sqrtN):
            self.sqrtFactInv[i] = self.factInv[i] if self.factInv[i] != None else pow(self.fact[i], self.mod - 2, self.mod)
        self.inv = [None]*(N+1)
        for i in range(1,self.sqrtN):
            self.inv[i] = pow(i, self.mod - 2, self.mod)
        for i in range(self.sqrtN,N+1):
            self.inv[i] = 1 if i == 1 else (self.sqrtFactInv[i//self.sqrtN] * self.inv[i % self.sqrtN] * self.inv[i//self.sqrtN]) % self.mod

    def combination(self, N, K):
        if K < 0 or N < K:
            return 0
        if K > N:
            K = N - K
            # return 0
        return self.fact[N]*self.factInv[K]*self.factInv[N-K] % self.mod

    def permutation(self, N, K):
        if K < 0 or N < K:
            return 0
        if K > N:
            K = N - K
            # return 0
        return self.fact[N]*self.factInv[N-K] % self.mod

    def summation(self, N, K):
        return (self.combination(N+K, K+1) - self.combination(N, K+1)*self.fact[K+1]) % self.mod

