@cache
def dp(state, N, M):
    if N + M == 0:
        return 0
    state = state.copy()

    first = sys.maxsize
    for go in range(N + M):
        if go < N:
            play = A[go]
        else:
            play = B[go - N]

        if state[idx[play]]:
            if play < max_cards[-1]:
                for c in max_cards[idx[play] + 1:]:
                    if not state[idx[c]]:
                        break
                    state[idx[c]] = True
            if play > 1:
                for c in list(state):
                    if c < play:
                        state.pop(c)
        else:
            state[idx[play]] = True

        if dp(tuple(state.values()), N - int(go < N), M - int(go >= N)) == 0:
            first = min(first, go)

    return first

def main() -> None:
    result = (
        "Aoki" if dp(tupleFalse for _ in max_cards), len(A), len(B)) >= len(A) + len(B)
        else "Takahashi"
    )
