class HeavyLightDecomposition:
    # Time Complexity: O(NlogN) for decomposition, O(logN) for each query
    # Space Complexity: O(NlogN)
    # Restrictions: point_query

    def __init__(self, graph) -> None:
        self.n = len(graph)
        self.log = self._init_log(self.n)
        self.par = [0] * self.n  # par[i]:i node's parent
        self.head = [i for i in range(self.n)]  # head[i]:i node's group head
        self.decompose(graph)

        # node value or vertex value
        self.v = [0] * self.n
        self.order = []
        self.num = [0] * self.n

        self.lazy = [0] * (self.n * 4)
        self.tree = [0] * (self.n * 4)

        # segment tree
        self.build()

    def _init_log(self, n):
        # Least Integer Exponent
        log = [0] * (n + 1)
        x = 1
        for i in range(1, n + 1):
            if 2 * x <= i:
                x *= 2
                log[i] = log[x] + 1
            else:
                log[i] = log[x]
        return log

    def decompose(self, graph):
        self._dfs(graph, 0, -1)
        self._hld(0, 0)
        return self.order

    def _get_path(self, path, root):
        return [self.head[i] for i in path if self.head[i]==root]

    def _dfs(self, graph, n, par):
        # post_order is the same as self.order
        self.post_order = []
        self.par[n] = par
        ret = 0

        for node, _ in graph[n].items():
            if node == par:
                continue
            self._dfs(graph, node, n)
            ret += self.post_order[0]
        self.post_order.insert(0, max(ret, self.v[n]))

    def _hld(self, n, g):
        self.order.append(n)
        self.num[n] = len(self.order) - 1
        sz = 0
        maxWeight = 0
        for node, weight in Graph[n].items():
            if self.par[n] == node:
                continue
            if graph[n][node] > maxWeight:
                sz = node
                maxWeight = graph[n][node]

        if sz > 0:
            for node in self._get_path(self.order, self.head[n]):
                self.head[node] = self.head[n]

            self._hld(sz, g)

        for node, weight in Graph[n].items():
            if self.par[n] == node or sz == node:
                continue
            g_node = max(maxWeight, Graph[n][node])
            self.head[node] = node
            self._hld(node, g_node)

    def build(self):
        self._build(1, 0, len(self.order) - 1)

    def _build(self, p, l, r):
        if l == r:
            self.tree[p] = self.env_num(self.v[self.order[l]])
            return self.tree[p]
        else:
            m = (l + r) // 2
            self._build(2 * p, l, m)
            self._build(2 * p + 1, m + 1, r)
            self.tree[p] = self.env_num(self.tree[2 * p], self.tree[2 * p + 1])
            return self.tree[p]

    def env_num(self, *args):
        if len(args) == 1:
            if self.arity == 1:
                return args[0]
            return self.op(args[0], self.id)
        return self.op(m1, m2)

    def query(self, start, end):
        start = self.num[start]
        end = self.num[end]
        if start > end:
            return self.id
        return self._query(1, 0, len(self.order) - 1, start, end)

    def _query(self, p, l, r, start, end):
        if r < start or l > end:
            return self.id
        if start <= l and r <= end:
            return self.tree[p]
        m = (l + r) // 2
        left = self._query(2 * p, l, m, start, end)
        right = self._query(2 * p + 1, m + 1, r, start, end)
        return self.env_num(left, right)

    def update(self, index, x):
        index = self.num[index]
        self._update(1, 0, len(self.order) - 1, index, index, x)
        self.v[index] = x

    def _update(self, p, l, r, start, end, x):
        if r < start or l > end:
            return
        if start <= l and r <= end:
            self.lazy[p] += x
            self.tree[p]  += x * (r - l + 1)
            return

        m = (l + r) // 2
        self._update(2 * p, l, m, start, end, x)
        self._update(2 * p + 1, m + 1, r, start, end, x)
        self.tree[p] = self.tree[2 * p] + self.tree[2 * p + 1]

    def op(x, y):
        return min(x, y)

    def id():
        return float("inf")

    def env_num(self, x):
        return bisect.bisect_right(l, x) - 1

