import sys

def read_ints():
    return map(int, input().split())

def main(*argv):
    N, K = read_ints()
    A = list(map(int, input().split()))

    # calculate occurrences of bits at respective positions
    bit_counts = [0] * 60
    for a in A:
        for bit in range(60):
            if a & (1 << bit):
                bit_counts[bit] += 1

    # sort bit counts when there is odd number of them occurrence
    odd_indices = [bit for bit, cnt in enumerate(bit_counts) if cnt % 2]
    odd_indices.sort(key=lambda bit: -bit_counts[bit])

    result = 0
    for bit in range(59, 0, -1):
        # 0 at bit position bit
        c0 = 0
        for bit_odd in odd_indices:
            if bit_odd >> 1 >= bit:
                c0 += 1
        # if we can pick c0 <= k-1 vectors with zero at bit position bit
        # choose c0 vectors + vector with 1 at bit position bit
        result |= (1 << bit) * (c0 <= K - 1)

    for b in range(60):
        if result & (1 << b):
            if bit_counts[b] < K:
                for ab in A:
                    if ab & (1 << b):
                        A.remove(ab)
                        break

    result ^= A[0]
    print(result)
    return

