class Solution:
    def makeSmallestPalindrome(self, s: str) -> str:
        s = list(s)
        n = len(s)

        for i in range(n // 2):
            if s[i] != s[n - i - 1]:
                # Choose the lexicographically smaller character
                if s[i] < s[n - i - 1]:
                    s[n - i - 1] = s[i]
                else:
                    s[i] = s[n - i - 1]

        return ''.join(s)
