        for i in range(n):
            for j in range(i + 1, n):
                if (num[i] == '2' and num[j] == '5') or (num[i] == '5' and num[j] == '0') or (num[i] == '7' and num[j] == '5') or (num[i] == '0' and num[j] == '0'):
