        if sick[-1] < n - 1:
            gaps.append(n - 1 - sick[-1])

        # Calculate the factorial and modular inverse factorial
        def factorial(n):
            result = 1
            for i in range(2, n + 1):
                result = (result * i) % MOD
            return result

        def mod_inverse(x):
