    def maxPartitionsAfterOperations(self, s: str, k: int) -> int:
        from collections import Counter

        def count_partitions(s, k):
            partitions = 0
            i = 0
            while i < len(s):
                distinct_chars = set()
                while i < len(s) and (len(distinct_chars) < k or s[i] in distinct_chars):
                    distinct_chars.add(s[i])
                    i += 1
                partitions += 1
            return partitions

