            while queue:
                node = queue.popleft()
                current_time = times[node]

                for neighbor in graph[node]:
                    if times[neighbor] == -1:
                        if neighbor % 2 == 0:
                            times[neighbor] = current_time + 2
                        else:
                            times[neighbor] = current_time + 1
                        queue.append(neighbor)

            return max(times)

