                    moves = bfs(kx, ky, px, py)
                    remaining_positions = positions[:i] + positions[i+1:]
                    min_moves = min(min_moves, moves + minimax(px, py, remaining_positions, not is_alice_turn))
                return min_moves

        return minimax(kx, ky, positions, True)
