from typing import List

class Solution:
    def maxScore(self, nums: List[int]) -> int:
        def lcm(a, b):
            return a * b // gcd(a, b)

        def gcd_list(arr):
            return reduce(gcd, arr)

        def lcm_list(arr):
            return reduce(lcm, arr)

        n = len(nums)

        if n == 1:
            return nums[0] * nums[0]

        # Calculate the GCD and LCM of the entire array
        total_gcd = gcd_list(nums)
        total_lcm = lcm_list(nums)

        # Initial factor score without removing any element
        max_score = total_gcd * total_lcm

        # Try removing each element and calculate the factor score
        for i in range(n):
            temp_nums = nums[:i] + nums[i+1:]
            if temp_nums:
                current_gcd = gcd_list(temp_nums)
                current_lcm = lcm_list(temp_nums)
                max_score = max(max_score, current_gcd * current_lcm)

        return max_score

class Solution:
    def findAnswer(self, parent: List[int], s: str) -> List[bool]:
        from collections import defaultdict

        # Build the tree from the parent array
        tree = defaultdict(list)
        for child, par in enumerate(parent):
            if par != -1:
                tree[par].append(child)

        # Function to perform DFS and build the dfsStr
        def dfs(x):
            nonlocal dfsStr
            for y in sorted(tree[x]):
                dfs(y)
            dfsStr += s[x]

        # Function to check if a string is a palindrome
        def is_palindrome(string):
            return string == string[::-1]

        n = len(parent)
        answer = [False] * n

        # Iterate over each node and check if the dfsStr is a palindrome
        for i in range(n):
            dfsStr = ""
            dfs(i)
            answer[i] = is_palindrome(dfsStr)

        return answer

