    def countValidSelections(self, nums: List[int]) -> int:
        def can_zero_out(nums, start, direction):
            n = len(nums)
            curr = start
            nums_copy = nums[:]
            while 0 <= curr < n:
                if nums_copy[curr] == 0:
                    curr += direction
                else:
                    nums_copy[curr] -= 1
                    direction = -direction
                    curr += direction
