    def minDifference(self, nums: List[int]) -> int:
        def find_min_max(nums):
            min_val = float('inf')
            max_val = float('-inf')
            for num in nums:
                if num != -1:
                    min_val = min(min_val, num)
                    max_val = max(max_val, num)
