                    if prev is not None:
                        max_diff = max(max_diff, abs(prev - x), abs(prev - y))
                    prev = min(x, y)
                else:
                    if prev is not None:
                        max_diff = max(max_diff, abs(prev - num))
                    prev = num
            return max_diff

        min_val, max_val = find_min_max(nums)

        if min_val == float('inf'):
            return 0

        x = min_val
        y = max_val

        return calculate_max_diff(nums, x, y)
