def count_valid_parenthesis_strings(S):
    n = len(S)
    dp = [[0] * (n + 1) for _ in range(n + 1)]
    dp[0][0] = 1

    for i in range(1, n + 1):
        for j in range(n + 1):
            if S[i - 1] == '(':
                if j > 0:
                    dp[i][j] = dp[i - 1][j - 1]
            elif S[i - 1] == ')':
                if j < n:
                    dp[i][j] = dp[i - 1][j + 1]
            else:  # S[i - 1] == '?'
                if j > 0:
                    dp[i][j] = dp[i - 1][j - 1]
                if j < n:
                    dp[i][j] = (dp[i][j] + dp[i - 1][j + 1]) % MOD

