    checkpoints = []

    for i in range(N):
        x = int(data[2 * i + 1])
        y = int(data[2 * i + 2])
        checkpoints.append((x, y))

    result = dijkstra_min_path_with_penalty(N, checkpoints)
    print(f"{result:.20f}")

if __name__ == "__main__":
