            if is_valid(nx, ny):
                count += dfs(nx, ny, steps + 1)
        grid[x][y] = '.'  # Unmark the cell
        return count

    total_paths = 0
    for i in range(H):
        for j in range(W):
            if grid[i][j] == '.':
                total_paths += dfs(i, j, 0)

    return total_paths

