        Y = int(data[index + 1])
        C = data[index + 2]
        index += 3

        if C == 'B':
            if X not in black_rows:
                black_rows[X] = Y
            else:
                black_rows[X] = max(black_rows[X], Y)

            if Y not in black_cols:
                black_cols[Y] = X
            else:
                black_cols[Y] = max(black_cols[Y], X)
        else:
            if X not in white_rows:
                white_rows[X] = Y
            else:
                white_rows[X] = min(white_rows[X], Y)

            if Y not in white_cols:
                white_cols[Y] = X
            else:
