import math

MOD = 998244353

def count_divisors(x):
    count = 0
    for i in range(1, int(math.sqrt(x)) + 1):
        if x % i == 0:
            count += 1
            if i != x // i:
                count += 1
    return count

def solve(N, M):
    # Precompute the number of divisors for all products up to M^N
    max_product = M ** N
    divisor_counts = [0] * (max_product + 1)

