                    seen_in_subarray.add(nums[k])

                if valid:
                    temp_count = count.copy()
                    for num in seen_in_subarray:
                        count[num] = count.get(num,0) + 1
                    ans = (ans + solve(j + 1)) % MOD
                    count = temp_count

            dp[i] = ans
            return ans

        result = solve(0)
        return result

