        points = 0
        marked = [False] * n

        sorted_enemies = sorted(range(n), key=lambda i: enemyEnergies[i])


        while True:
            best_enemy = -1
            for i in sorted_enemies:
                if not marked[i] and currentEnergy >= enemyEnergies[i]:
                    best_enemy = i
                    break

            if best_enemy == -1:
                break

            currentEnergy -= enemyEnergies[best_enemy]
            points += 1


