                while nums[i] > nums[i + 1]:
                    divisor = get_greatest_proper_divisor(nums[i])
                    if divisor == 1 and nums[i] > nums[i+1]: #Handle case where no proper divisor exists and num[i] > num[i+1]
                        operations = -1
                        break
                    nums[i] //= divisor
                    operations += 1
                if operations == -1:
